import axios from 'axios';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
export default {
  data() {
    return {
      prenote1Contents: '',
      prenote2Contents: ''
    };
  },
  methods: {
    handlePrenote1Upload(event) {
      const file = event.target.files[0];
      const reader = new FileReader();
      reader.onload = () => {
        this.prenote1Contents = reader.result;
      };
      reader.readAsText(file);
    },
    handlePrenote2Upload(event) {
      const file = event.target.files[0];
      const reader = new FileReader();
      reader.onload = () => {
        this.prenote2Contents = reader.result;
      };
      reader.readAsText(file);
    },
    submit: function () {
      axios.post('http://localhost:5000/prenote', {
        prenote1: this.prenote1Contents,
        prenote2: this.prenote2Contents
      }, {
        responseType: 'blob' // indicate that you are expecting a binary response
      }).then(function (response) {
        const csvFile = new Blob([response.data], {
          type: 'text/csv'
        });
        const reader = new FileReader();
        reader.onload = function (event) {
          const csvData = event.target.result;

          // Create a new jsPDF instance
          const doc = new jsPDF();

          // Convert the CSV data into an array of arrays
          const rows = csvData.split('\n').map(row => row.split(','));

          // Define the table headers
          const headers = rows.shift();

          // Define the table configuration
          const tableConfig = {
            head: [headers],
            // Use the headers as the table header
            body: rows,
            styles: {
              textColor: 'black'
            },
            alternateRowStyles: {
              fillColor: '#D3D3D3'
            },
            // Use the remaining rows as the table body

            didDrawCell: data => {
              const cellText = data.cell.text;
              const highlightTexts = ['BILLY', 'JACK']; // Specify the texts to highlight

              // const cellWidth = data.cell.width;
              const cellHeight = data.cell.height;
              const cellX = data.cell.x;
              const cellY = data.cell.y;
              const originalTextColor = data.doc.getTextColor(); // Store the original text color

              highlightTexts.forEach(highlightText => {
                const highlightIndex = cellText.indexOf(highlightText);
                if (highlightIndex !== -1) {
                  const beforeText = cellText.slice(0, highlightIndex);
                  const afterText = cellText.slice(highlightIndex + highlightText.length);
                  data.doc.setTextColor(originalTextColor); // Set the text color for the non-highlighted text
                  data.doc.text(beforeText, cellX, cellY + 5); // Display the non-highlighted text

                  data.doc.setFillColor('#FFFF00'); // Set the highlight color
                  data.doc.setTextColor('#000000'); // Set the text color for highlighted text

                  const highlightX = cellX + data.doc.getTextWidth(beforeText); // Calculate the x coordinate for the highlighted text
                  const highlightY = cellY + 5; // Use the same y coordinate as the non-highlighted text

                  data.doc.rect(highlightX, highlightY, data.doc.getTextWidth(highlightText), cellHeight, 'F'); // Draw the highlight rectangle
                  data.doc.text(highlightText, highlightX, highlightY + cellHeight / 2, {
                    align: 'left',
                    baseline: 'middle'
                  }); // Display the highlighted text

                  data.doc.setTextColor(originalTextColor); // Set the text color for the remaining text
                  data.doc.text(afterText, highlightX + data.doc.getTextWidth(highlightText), cellY + 5); // Display the remaining text
                }
              });
            }
          };

          // Generate the table in the PDF document
          doc.autoTable(tableConfig);

          // Save the PDF or open it in a new browser tab
          doc.save('table.pdf');
        };
        reader.readAsText(csvFile);
      }).catch(function (error) {
        console.log(error);
      });
    }
  }
};
